@import url('https://fonts.googleapis.com/css2?family=Reem+Kufi&display=swap');

* {
 margin: 0;
 padding: 0;
}

*,
*::after,
*::before {
 box-sizing: inherit;
}

html {
 box-sizing: border-box;
 font-size: 62.5%;
}

body {
 background: $defaultBodyColor;
 transition: background 1s ease-in-out;
 overflow: hidden;
}

.container {
 display: grid;
 grid-template-columns: 4fr 7em;
 //  grid-template-rows: 6em 1fr;
 gap: 0px 0px;
 grid-template-areas:
  'header header '
  'mainContent social';
 padding: 1em 2em;
 height: 100vh;
}

.header {
 //  border: $borderTest;
 grid-area: header;
 @include flexCentered;
 justify-content: space-between;
 padding: 0 2em;
 &__logo-container {
  img {
   width: 5em;
  }
  cursor: pointer;
  &:hover {
   transform: scale(1.08);
  }
 }
 &__contact {
  padding: 0.6em 1.9em;
  border-radius: 1.5em;
  border: none;
  background: $contactBtnColor;
  color: #d6d6d6;
  font-family: 'Reem Kufi', sans-serif;
  //   font-weight: bold;
  letter-spacing: 1px;

  margin-right: 2em;
  cursor: pointer;
  @include transition-fast;
  &:hover {
   background-color: $contactBtnColorHover;
   color: #2e2e2e;
   box-shadow: 0px 2px 1px rgba(0, 0, 0, 0.25);
  }
