sources.src\\sass\\_mixins.scss:
@mixin BorderTestSection {
 border-top: 2px solid rgb(243, 7, 7);
 border-bottom: 2px solid rgb(209, 34, 22);
}

@mixin flexCentered {
 display: flex;
 display: -webkit-flex;
 display: -moz-flex;
 display: -ms-flex;
 justify-content: center;
 align-items: center;
}

@mixin transition-ease {
 transition: all 400ms ease-in-out;
}
@mixin transition-fast {
 transition: all 300ms ease-in-out;
}

@mixin dropShadow {
 box-shadow: 0 3px 1px #111111;
}

@mixin projectBtnDefault {
 border-radius: 2px;
 padding: 0.6em 2em;
 border: none;
 background: #e03442;
 opacity: 0.3;
 color: #555;
 cursor: pointer;
 @include transition-ease;
 &:hover {
  opacity: 1;
  color: $activeBtnTextColor;
  font-weight: bold;
  letter-spacing: 1px;
  padding: 0.6em 0;
  box-shadow: 0 1px 1px #1b1b1be3;
 }
}
@mixin tubeFilter {
 filter: drop-shadow(0 -3px 2px #fd6b6b) drop-shadow(0 3px 2px #fd6b6b);
}

//default tube style

@mixin defaultTubeTop {
 border-top: 4px solid $tubeColor;
 border-right: 4px solid $tubeColor;
 border-bottom: 4px solid $tubeColor;
}
@mixin defaultTubeMiddle {
 border-bottom: 4px solid $tubeColor;
 border-right: 4px solid $tubeColor;
}

@mixin defaultTubeBottom {
 border-bottom: 4px solid $tubeColor;
 border-right: 4px solid $tubeColor;
}

//activeTubes

@mixin tubeTop {
 border-top: 4px solid $activeTubeColor !important;
 border-right: 4px solid $activeTubeColor !important;
 border-bottom: 4px solid $activeTubeColor !important;
 @include tubeFilter();
}

@mixin activeTubeMiddle {
 @include tubeFilter();
 border-bottom: 4px solid $activeTubeColor !important;
 border-right: 4px solid $activeTubeColor !important;
}

@mixin activeTubeBottom {
 @include tubeFilter();
 border-right: 4px solid $activeTubeColor !important;
 border-bottom: 4px solid $activeTubeColor !important;
}

//active Project-tube style

@mixin activeTubePromo {
 border-top: 4px solid $activeTubeColor;
 @include tubeFilter();
}

@mixin activeTubeTransactional {
 border-bottom: 4px solid $activeTubeColor;
 @include tubeFilter();
}

@mixin activeTubeNewsletter {
 border-bottom: 4px solid $activeTubeColor;
 @include tubeFilter();
}

@mixin activeTubeLandingPage {
 border-bottom: 4px solid $activeTubeColor;
 @include tubeFilter();
}

@mixin defaultCardStyle {
 width: 51em;
 height: 41vh;
 border-radius: 1em;
 background-color: #e02a3a;
 display: flex;
 flex-direction: column;
 padding: 2em 1em;
 transition: all 0.6s;
 cursor: pointer;
 &:hover {
  background-color: #971919;
 }
}

//active body color

@mixin activeBody {
 background: $activeBodyColor;
}

// active cardSide Style - mobile

@mixin activeCardSideColor {
 .main-content__card-container__card div.main-content__card-sideB {
  background-color: $activeCardSideColor;
 }
 .main-content__card-container__card div.main-content__card-sideA {
  background-color: $activeCardSideColor;
 }
}

//active button style

@mixin activeBtn {
 //  background-color: #d83340;
 opacity: 1;
 color: #e9e9e9;
 //  border: 2px solid #eb8a9196;
 //  filter: drop-shadow(0 0 3px #fd6b6b80);
 //  box-shadow: 1px 1px 2px 0px rgb(139, 9, 9);
 transition: opacity 300ms ease-in-out;
 &:hover {
  opacity: 1;
  cursor: pointer;
  font-weight: bold;
  box-shadow: 1px 1px 2px 0px rgb(139, 9, 9);
 }
}

//card rotation

@mixin cardRotation {
 transform: rotateX(-180deg);
 div.main-content__card-sideA {
  z-index: -2;
  visibility: hidden;
 }
 div.main-content__card-sideB {
  transform: rotateX(190deg);
  &:hover {
   background-color: rgb(145, 15, 47);
  }
 }
}

//card hide

@mixin cardHide {
 div.main-content__card-container__card {
  display: none;
 }
}

//default project activation

@mixin defaultProjectActivation {
 @include activeBody;
 section.main-content__screen-container {
  display: inline-block;
 }
 @include cardHide;
}

sources.src\\sass\\_mainContent.scss:
.main-content {
 height: 80vh;
 display: grid;
 grid-template-columns: 18em 4fr;
 grid-template-areas: 'content content';
 margin-top: 3em;
 padding: 2em;
 &__btn-container {
  grid-area: projects;
  display: flex;
  flex-direction: column;
  align-items: end;

  button.project-btn {
   margin: 1em 0;
  }

  button#transactional {
   position: relative;
   top: 1.3em;
  }
  button#newsletter {
   position: relative;
   top: 2.8em;
  }
  button#landingPage {
   position: relative;
   bottom: -22em;
  }
 }
 &__card-container {
  grid-area: content;
  padding-top: 3em;
  .ghost-div__container {
   height: 100%;
   & div.ghost-div {
    width: 70vw;
    height: 10vh;
   }

   div.ghost-div:nth-child(1) {
    @include defaultTubeTop;
   }
   div.ghost-div:nth-child(2) {
    @include defaultTubeMiddle;
    border-bottom-left-radius: 1px;
   }
   div.ghost-div:nth-child(3) {
    border-right: 4px solid $tubeColor;
    @include tubeFilter;
   }

   div.ghost-div:last-child {
    @include defaultTubeBottom;
    position: relative;
    height: 30vh;
    button.project-btn#landingPage {
     position: absolute;
     bottom: 0;
    }
   }
  }

  &__card {
   @include transition-ease;
   position: absolute;
   right: 11vw;
   top: 29vh;
  }
 }
 &__card-sideA {
  position: relative;
  @include defaultCardStyle;
  &-name {
   width: 18vw;
  }

  &-version {
   width: 4vw;
   position: absolute;
   right: 24px;
   bottom: 21px;
  }
 }

 &__card-sideB {
  position: absolute;
  top: 0;
  color: rgb(243, 243, 243);
  z-index: -1;
  height: auto;
  @include defaultCardStyle;
  &__header-container {
   font-family: 'Nova Square', cursive;
   padding: 0.4em;
   font-size: 2.5rem;
   border-bottom: 2px solid white;
   text-align: right;
  }

  &__text-container {
   padding: 1em;
   margin-top: 2em;
   font-size: 1.5rem;
   line-height: 26px;
   font-family: 'Poppins', sans-serif;
   font-weight: bold;
  }
 }
}

//social media default stylings
.social-media {
 align-self: end;
 align-items: last baseline;
 display: flex;
 flex-direction: column;
 margin-bottom: 1.5em;

 & > a > img {
  width: 3.6em;
  margin: 0.4em 0;
  transition: -webkit-filter 0.2s ease-in;

  &:hover {
   -webkit-filter: blur(0px) brightness(2.7) contrast(7) grayscale(0)
    hue-rotate(190deg) invert(0) opacity(100%) saturate(6) sepia(6.29)
    drop-shadow(0px 1px 6px $activeSocialMediaColor);
   filter: blur(0px) brightness(2.7) contrast(7) grayscale(0) hue-rotate(190deg)
    invert(0) opacity(100%) saturate(6) sepia(6.29)
    drop-shadow(0px 1px 6px $activeSocialMediaColor);
  }
 }
}

//svg for mobile animations

div.svg-arrow__container {
 position: absolute;
 padding: 1em;
 left: 67%;
 bottom: 26em;
 @include transition-ease;
 display: none;
 & > img {
  width: 4em;
  position: absolute;
  transition: all 5s ease;
 }
}

