@media screen and (max-width: 500px) {
 body {
  overflow-y: hidden;
 }

 .header {
  padding: 0 1.5em !important;
  &__logo-container {
   position: relative;
   z-index: 1;
  }

  &__contact {
   position: relative;
   z-index: 1;
  }
 }
 div.main-content__screen.project {
  top: 20vh;
 }
 div.main-content__screenproject-card__wrapper {
  flex-direction: row;
  padding-top: 0;
 }
 div.container {
  padding: 1em 0;
 }
 div.main-content__card-container__card {
  //   right: 4vw;
  width: 90%;
 }

 div.main-content__card-sideA,
 div.main-content__card-sideB {
  width: 100%;
  height: 32vh;
 }

 .main-content__card-sideB__text-container {
  margin-top: 0;
 }

 img.main-content__card-sideA-version {
  width: 10vw;
 }

 img.main-content__card-sideA-name {
  width: 32vw;
 }

 .main-content__btn-container-tray {
  width: 100%;
  position: absolute;
  left: 0;
  height: 60vh;
  background-color: #111010d9;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  bottom: -52%;
  transition: all 1s;
  cursor: pointer;
  &:hover {
   background-color: rgb(26, 26, 26);
  }
  &__top-notch {
   width: 10em;
   display: inline-block;
   height: 0.8em;
   background: $trayNotchBGcolor;
   border-radius: 4px;
   margin-left: 2.5em;
   cursor: pointer;
   @include transition-fast;
   &:hover {
    background-color: $trayNotchBGcolor__hover;
    box-shadow: 0px 0px 5px 3px #65dd777a;
   }
  }

  .main-content__btn-container-tray__header-container {
   color: rgb(238, 237, 237);
   font-size: 1.2rem;
   font-family: 'Nova Square', cursive;
   display: flex;
   justify-content: space-around;
   width: 100%;
   letter-spacing: 1px;
   height: 5em;
   padding-top: 2em;
  }

  &__container {
   $trayBtnBorderColor: rgba(110, 104, 52, 0.822);
   height: 65%;
   width: 90%;
   margin: 0 auto;
   margin-top: 2em;
   @include flexCentered;
   flex-direction: column;
   border-radius: 4px;
   box-shadow: 0 0 16px $trayBtnBorderColor;

   button.project-btn__tray {
    margin: 1em 0;
    width: 90%;
    box-shadow: 0px 0px 4px grey;
    @include projectBtnDefault;
    color: rgb(255, 255, 255);
    font-weight: bold;
    letter-spacing: 1px;
    @include transition-fast;
    &:hover {
     opacity: 1;
     box-shadow: 0px 0px 8px rgb(243, 129, 199);
     color: rgb(53, 53, 53);
    }
   }
  }

  aside.social-media-tray {
   position: absolute;
   bottom: 0;
   margin: 1em auto;

   & > a > img {
    width: 3.6em;
    margin: 1em 0.6em;
    transition: -webkit-filter 0.2s ease-in;
    transition: filter 0.2s ease-in;
    -webkit-filter: blur(0px) brightness(5) contrast(1) grayscale(0)
     hue-rotate(190deg) invert(0) opacity(80%) saturate(1) sepia(0)
     drop-shadow(0px 1px 1px $activeSocialMediaColor);
    filter: blur(0px) brightness(3.2) contrast(1) grayscale(0)
     hue-rotate(190deg) invert(0) opacity(80%) saturate(1) sepia(0)
     drop-shadow(0px 1px 1px $activeSocialMediaColor);

    &:hover {
     -webkit-filter: blur(0px) brightness(5) contrast(7) grayscale(0)
      hue-rotate(190deg) invert(0) opacity(100%) saturate(6) sepia(6.29)
      drop-shadow(0px 1px 4px $activeSocialMediaColor);
     filter: blur(0px) brightness(5) contrast(7) grayscale(0) hue-rotate(190deg)
      invert(0) opacity(100%) saturate(6) sepia(6.29)
      drop-shadow(0px 1px 4px $activeSocialMediaColor);
    }
   }
  }
 }

 aside.social-media {
  display: none;
 }

 .main-content__btn-container,
 div.ghost-div__container {
  display: none;
 }

 .project-active
  article.projectScreen__project-project
  .projectScreen__project-project__wrapper {
  margin-top: 0;
 }

 div.landingPage > img#img-techwearLandingPage,
 img#img-qpick {
  width: 100%;
  min-width: 400px;
  height: 256px;
 }

 div.landingPage.slide4,
 div.landingPage.slide2 {
  background: #201e1e;
  border-radius: 0.6em;
  width: 100%;
  padding: 2em;
  z-index: 1;
 }

 .projectScreen__project-project__header {
  position: relative;
  left: -50%;
 }

 div.projectScreen__project-project__wrapper div.promo-img {
  position: relative;
  left: 40%;
 }
}

