 section.projectScreen__project i.fa.fa-angle-left,
 section.projectScreen__project i.fa.fa-angle-right {
  margin: 0.6em;
 }

 div.projectScreen__project-project__wrapper > img {
  width: 100%;
  min-width: 70vw;
  margin-left: 10vw;
 }
}

@media screen and (max-width: 390px) {
