  font-size: 4vw;
 }
 h1.main-content__card-sideB-heading {
  font-size: 6vw;
 }
 div.projectScreen__project-project__wrapper div.promo-img {
  position: relative;
  left: 44%;
 }

 div.main-content_indicator-container {
  top: 13vh;
  width: 100%;
  left: 6vw;
